import { logDOM } from '@testing-library/dom';
import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom';
import './Navbar.css';
import {Button} from './Button'
import { cleanup } from '@testing-library/react';


function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    //When Scroll down Navbar Hides
    const [show, setShow] = useState(true);
    const controlNavbar = () => {
        if (window.scrollY > 100) {
            setShow(false);
        } else {
            setShow(true);
        }
    }

    useEffect(() => {
        window.addEventListener('scroll', controlNavbar)
        return () => {
            window.addEventListener('scroll', controlNavbar)
        }
    }, [])


    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false);
        } else {
            setButton(true);
        }
    };

    window.addEventListener('resize', showButton);

    return(
    <nav className= {`nav ${show && 'navbar'}`}>
        <div className="navbar-container">
            <Link to='/'>
                <img src="icons/logo.png" alt="Logo" className="navbar-logo" onClick={closeMobileMenu}/>
            </Link>
            <div className="menu-icon" onClick={handleClick}>
                <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
            </div>

            <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                <li className='nav-item'>
                    <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                        HOME
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to='/about' className='nav-links' onClick={closeMobileMenu}>
                        ABOUT
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to='/projects' className='nav-links' onClick={closeMobileMenu}>
                        PROJECTS
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to='/contact' className='nav-links' onClick={closeMobileMenu}>
                        CONTACT
                    </Link>
                </li>
                <li>
                    <Link to='/Resume' className='nav-links-mobile' onClick={closeMobileMenu}>
                        RESUME
                    </Link>
                </li>

            </ul>
            {button && <Button buttonStyle='btn--outline'>RESUME</Button>}
        </div>

    </nav>

    )
}

export default Navbar
