import { logDOM } from '@testing-library/dom';
import React, { useState } from 'react';
import './Navbar.css';
import {Button} from './Button'
import { Link, animateScroll as scroll } from "react-scroll";


function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false);
        } else {
            setButton(true);
        }
    };

    window.addEventListener('resize', showButton);

    return(
    <nav className="navbar">
        <div className="navbar-container">
            <Link to='/'>
                <img src="icons/logo.png" alt="Logo" className="navbar-logo" onClick={closeMobileMenu}/>
            </Link>
            <div className="menu-icon" onClick={handleClick}>
                <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
            </div>

            <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                <li className='nav-item'>
                    <Link to='/' className='nav-links' onClick={closeMobileMenu}
                    spy={true}
                    smooth={true}
                    offset={-70}
                    duration={500}>
                        HOME
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to="about-section" className='nav-links' onClick={closeMobileMenu}>
                        ABOUT
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to='/projects' className='nav-links' onClick={closeMobileMenu}>
                        PROJECTS
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to='/contact' className='nav-links' onClick={closeMobileMenu}>
                        CONTACT
                    </Link>
                </li>
                <li>
                    <Link to='/Resume' className='nav-links-mobile' onClick={closeMobileMenu}>
                        RESUME
                    </Link>
                </li>

            </ul>
            {button && <Button buttonStyle='btn--outline'>RESUME</Button>}
        </div>

    </nav>

    )
}

export default Navbar
